import React from 'react';
import axios from 'axios';

const RecipeItem = ({ recipe, onAddToFavorites }) => {
  const handleAddToFavorites = async () => {
    try {
      await axios.post(`${process.env.REACT_APP_API_URL}/api/favorites/add`, { recipeId: recipe.id || recipe._id }, { withCredentials: true });
      onAddToFavorites(recipe.id || recipe._id);
    } catch (error) {
      console.error('Error adding to favorites:', error);
    }
  };

  return (
    <div className="card mb-4">
      <img src={recipe.image_url} className="card-img-top" alt={recipe.title} />
      <div className="card-body">
        <h5 className="card-title">{recipe.title}</h5>
        <h6>Ingredients:</h6>
        <ul>
          {recipe.ingredients && recipe.ingredients.map((ingredient, index) => (
            <li key={index}>{ingredient}</li>
          ))}
        </ul>
        <h6>Instructions:</h6>
        <p>{recipe.instructions}</p>
        <button onClick={handleAddToFavorites} className="btn btn-primary">Add to Favorites</button>
      </div>
    </div>
  );
};

export default RecipeItem;
