import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Header from './components/Header';
import RecipeList from './components/RecipeList';
import Authentication from './components/Authentication';

const App = () => {
  const [recipes, setRecipes] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [user, setUser] = useState(null);

useEffect(() => {
  fetchRecipes().catch(console.error);
  checkCurrentUser().catch(console.error);
}, []);

// Function to check if a user is currently logged in
  const checkCurrentUser = async () => {
    try {
      const response = await axios.get(`${process.env.REACT_APP_API_URL}/api/users/current-user`, { withCredentials: true });
      setUser(response.data.user);
    } catch (error) {
      console.error('Error checking current user:', error);
    }
  };

  // ... (fetchRecipes and handleSearch remain the same)
  const fetchRecipes = async () => {
    try {
      setLoading(true);
      const response = await axios.get(`${process.env.REACT_APP_API_URL}/api/recipes`);
      setRecipes(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching recipes:', error);
      setError('Error loading recipes. Please try again later.');
      setLoading(false);
    }
  };

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

    // Added: Function to handle user logout
  const handleLogout = async () => {
    try {
      await axios.get(`${process.env.REACT_APP_API_URL}/api/users/logout`, { withCredentials: true });
      setUser(null);
    } catch (error) {
      console.error('Error logging out:', error);
    }
  };
  const filteredRecipes = recipes.filter((recipe) =>
    recipe.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div>
      <Header user={user} onLogout={handleLogout} /> {/* Pass user and logout function to Header */}
      <div className="container mt-4">
        {user ? ( // Conditional rendering based on user authentication
          <>
            <input
              type="text"
              className="form-control mb-4"
              placeholder="Search recipes..."
              value={searchTerm}
              onChange={handleSearch}
            />
            <RecipeList recipes={filteredRecipes} loading={loading} error={error} />
          </>
        ) : (
          <Authentication setUser={setUser} /> // Render Auth component if user is not logged in
        )}
      </div>
    </div>
  );
};

export default App;
