import React from 'react';

const Favorites = ({ favorites, onRemoveFromFavorites }) => {
  if (favorites.length === 0) {
    return <p>No favorite recipes yet.</p>;
  }

  return (
    <div className="container">
      <h1 className="mt-4 mb-4">My Favorites</h1>
      <div className="row">
        {favorites.map((recipe) => (
          <div className="col-md-4" key={recipe.id || recipe._id}>
            <div className="card mb-4">
              <img src={recipe.image_url} className="card-img-top" alt={recipe.title} />
              <div className="card-body">
                <h5 className="card-title">{recipe.title}</h5>
                <button
                  onClick={() => onRemoveFromFavorites(recipe.id || recipe._id)}
                  className="btn btn-danger"
                >
                  Remove from Favorites
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Favorites;
