import React, { useEffect, useState } from 'react';
import axios from 'axios';
import RecipeItem from './RecipeItem';

const RecipeList = () => {
  const [recipes, setRecipes] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchRecipes = async () => {
      try {
        const response = await axios.get(`${process.env.REACT_APP_API_URL}/api/recipes`);
        setRecipes(response.data);
      } catch (error) {
        console.error("Error fetching recipes:", error);
        setError('Error loading recipes. Please try again later.');
      }
    };

    fetchRecipes().catch(error => {
      console.error("Unhandled error in fetchRecipes:", error);
      setError('An unexpected error occurred. Please try again later.');
    });
  }, []);

  return (
    <div className="container">
      <h1 className="mt-4 mb-4">Recipes</h1>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <div className="row">
        {recipes.map((recipe) => (
          <div className="col-md-4" key={recipe.id}>
            <RecipeItem recipe={recipe} />
          </div>
        ))}
      </div>
    </div>
  );
};

export default RecipeList;
