import React, { useState } from 'react';

const RecipeItem = ({ recipe, isFavorite, onAddToFavorites }) => {
  const [showDetails, setShowDetails] = useState(false);

  const handleFavoriteClick = () => {
    if (!isFavorite) {
      onAddToFavorites(recipe.id);
    }
  };

  const toggleDetails = () => {
    setShowDetails(!showDetails);
  };

  console.log('Recipe data:', recipe);

  const renderIngredients = () => {
    if (Array.isArray(recipe.ingredients)) {
      return recipe.ingredients.map((ingredient, index) => (
        <li key={index}>{ingredient}</li>
      ));
    } else {
      return <li>No ingredients information available</li>;
    }
  };

  const renderInstructions = () => {
    if (typeof recipe.instructions === 'string') {
      // Split the instructions into paragraphs
      return recipe.instructions.split('\n').map((step, index) => (
        <p key={index}>{step}</p>
      ));
    } else {
      return <p>No instructions available</p>;
    }
  };

  return (
    <div className="card mb-4">
      <img src={recipe.image_url} className="card-img-top" alt={recipe.title} />
      <div className="card-body">
        <h5 className="card-title">{recipe.title}</h5>
        <button
          onClick={handleFavoriteClick}
          className={`btn ${isFavorite ? 'btn-success' : 'btn-primary'} mr-2`}
          disabled={isFavorite}
        >
          {isFavorite ? 'Added to Favorites' : 'Add to Favorites'}
        </button>
        <button onClick={toggleDetails} className="btn btn-info">
          {showDetails ? 'Hide Details' : 'Show Details'}
        </button>
        {showDetails && (
          <div className="mt-3">
            <h6>Ingredients:</h6>
            <ul>{renderIngredients()}</ul>
            <h6>Instructions:</h6>
            <div>{renderInstructions()}</div>
          </div>
        )}
      </div>
    </div>
  );
};

export default RecipeItem;
