import React, { useState } from 'react';
import axios from 'axios';

// Added: New component for handling user authentication
const Authentication = ({ setUser }) => {
  const [isLogin, setIsLogin] = useState(true);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  // Added: Handle form submission for login/register
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    try {
      const endpoint = isLogin ? '/api/users/login' : '/api/users/register';
      const response = await axios.post(`${process.env.REACT_APP_API_URL}${endpoint}`, {
        username,
        password
      }, { withCredentials: true });
      if (isLogin) {
        setUser(response.data.user);
      } else {
        setIsLogin(true);
      }
    } catch (error) {
      setError(error.response?.data?.message || 'An error occurred');
    }
  };

  // Added: Render login/register form
  return (
    <div className="container mt-5">
      <h2>{isLogin ? 'Login' : 'Register'}</h2>
      <form onSubmit={handleSubmit}>
        {/* ... form fields ... */}
        <button type="submit" className="btn btn-primary">{isLogin ? 'Login' : 'Register'}</button>
      </form>
      <p className="mt-3">
        {isLogin ? "Don't have an account? " : "Already have an account? "}
        <button className="btn btn-link p-0" onClick={() => setIsLogin(!isLogin)}>
          {isLogin ? 'Register' : 'Login'}
        </button>
      </p>
    </div>
  );
};

export default Authentication;
