import React from 'react';

// Updated: Added user and onLogout props
const Header = ({ user, onLogout }) => {
  return (
    <header className="bg-primary text-white p-3">
      <div className="container d-flex justify-content-between align-items-center">
        <h1>Simply Vegan</h1>
        <nav>
          <ul className="list-inline mb-0">
            <li className="list-inline-item"><a href="/" className="text-white">Home</a></li>
            {user && ( // Added: Conditional rendering for authenticated users
              <>
                <li className="list-inline-item"><a href="/favorites" className="text-white">Favorites</a></li>
                <li className="list-inline-item">
                  <button onClick={onLogout} className="btn btn-outline-light">Logout</button>
                </li>
              </>
            )}
          </ul>
        </nav>
      </div>
    </header>
  );
};

export default Header;
