import React, { useEffect, useState } from 'react';
import axios from 'axios';
import RecipeItem from './RecipeItem';

const Favorites = () => {
  const [favorites, setFavorites] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchFavorites();
  }, []);

  const fetchFavorites = async () => {
  try {
    setLoading(true);
    const response = await axios.get(`${process.env.REACT_APP_API_URL}/api/favorites`, { withCredentials: true });
    console.log('Favorites response:', response.data);
    setFavorites(response.data);
    setLoading(false);
  } catch (error) {
    console.error('Error fetching favorites:', error.response?.data || error.message);
    setError('Error loading favorites. Please try again later.');
    setLoading(false);
  }
};

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="container">
      <h1 className="mt-4 mb-4">My Favorites</h1>
      {favorites.length > 0 ? (
        <div className="row">
          {favorites.map((recipe) => (
            <div className="col-md-4" key={recipe.id || recipe._id}>
              <RecipeItem recipe={recipe} onAddToFavorites={() => {}} />
            </div>
          ))}
        </div>
      ) : (
        <p>No favorite recipes yet.</p>
      )}
    </div>
  );
};

export default Favorites;
