import React from 'react';
import RecipeItem from './RecipeItem';

const RecipeList = ({ recipes, loading, error, onAddToFavorites, onRemoveFromFavorites, favorites }) => {
  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div style={{ color: 'red' }}>{error}</div>;
  }

  return (
    <div className="container">
      <h1 className="mt-4 mb-4">Recipes</h1>
      {recipes && recipes.length > 0 ? (
        <div className="row">
          {recipes.map((recipe) => (
            <div className="col-md-4" key={recipe.id || recipe._id}>
              <RecipeItem recipe={recipe} onAddToFavorites={onAddToFavorites} />
            </div>
          ))}
        </div>
      ) : (
        <p>No recipes found</p>
      )}
    </div>
  );
};

export default RecipeList;
