import React, { useState, useEffect } from 'react';
import axios from 'axios';

const RecipeItem = ({ recipe, isFavorite, onAddToFavorites, onRemoveFromFavorites }) => {
  const [isAdded, setIsAdded] = useState(isFavorite);

  useEffect(() => {
    setIsAdded(isFavorite);
  }, [isFavorite]);

  const handleFavoriteClick = async () => {
    if (isAdded) {
      try {
        await onRemoveFromFavorites(recipe.id || recipe._id);
        setIsAdded(false);
      } catch (error) {
        console.error('Error removing from favorites:', error);
      }
    } else {
      try {
        await onAddToFavorites(recipe.id || recipe._id);
        setIsAdded(true);
      } catch (error) {
        console.error('Error adding to favorites:', error);
      }
    }
  };

  return (
    <div className="card mb-4">
      <img src={recipe.image_url} className="card-img-top" alt={recipe.title} />
      <div className="card-body">
        <h5 className="card-title">{recipe.title}</h5>
        <button
          onClick={handleFavoriteClick}
          className={`btn ${isAdded ? 'btn-success' : 'btn-primary'}`}
        >
          {isAdded ? (isFavorite ? 'Delete Favorite' : 'Favorite Added') : 'Add to Favorites'}
        </button>
      </div>
    </div>
  );
};

export default RecipeItem;
