import React, { useEffect, useState } from 'react';
import axios from 'axios';


const Favorites = () => {
  const [favorites, setFavorites] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    (async () => {
      try {
        await fetchFavorites();
      } catch (error) {
        console.error('Error fetching favorites:', error);
      }
    })();
  }, []);

  const fetchFavorites = async () => {
    try {
      setLoading(true);
      const response = await axios.get(`${process.env.REACT_APP_API_URL}/api/favorites`, { withCredentials: true });
      console.log('Favorites response:', response.data);
      setFavorites(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching favorites:', error.response?.data || error.message);
      setError('Error loading favorites. Please try again later.');
      setLoading(false);
    }
  };

  const handleRemoveFromFavorites = async (recipeId) => {
    try {
      await axios.delete(`${process.env.REACT_APP_API_URL}/api/favorites/remove/${recipeId}`, { withCredentials: true });
      setFavorites(favorites.filter(fav => (fav.id || fav._id) !== recipeId));
    } catch (error) {
      console.error('Error removing from favorites:', error);
    }
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="container">
      <h1 className="mt-4 mb-4">My Favorites</h1>
      {favorites.length > 0 ? (
        <div className="row">
          {favorites.map((recipe) => (
            <div className="col-md-4" key={recipe.id || recipe._id}>
              <div className="card mb-4">
                <img src={recipe.image_url} className="card-img-top" alt={recipe.title} />
                <div className="card-body">
                  <h5 className="card-title">{recipe.title}</h5>
                  <button
                    onClick={() => handleRemoveFromFavorites(recipe.id || recipe._id)}
                    className="btn btn-danger"
                  >
                    Remove from Favorites
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <p>No favorite recipes yet.</p>
      )}
    </div>
  );
};

export default Favorites;
